import { useState } from "react";
import "./Style.css"
const Contador = () => {
    const [numero, setNumero] = useState(0);

    const handleMinus = () => {
        setNumero(numero - 1);
    }
    const handlePlus = () => {
        setNumero(numero + 1);

    };

    return (
        <div className="contador">
            <h1>Counter</h1>
            <div className="cont-format">
                <button onClick={handleMinus}>-</button>
                <div>{numero}</div>
                <button onClick={handlePlus}>+</button>
            </div>
            <div className="card text-center">
        <div className="card-header">
            <h1>Simple react component with a counting function</h1>
        </div>
            <div className="card-body">
                <h2 className="card-title">This was made with the use of the useState react hook</h2>
                <p className="card-text">Main useState structure:
                </p>
                <a href="#" className="btn btn-primary">Go somewhere</a>
            </div>
            <div className="card-footer text-muted">
                
            </div>
            </div>
        </div>
    )
}
export default Contador;
