import { useState, useEffect } from "react";
import { api } from "../../api";
export const GalleryItems = () => {
    const [items, setItems] = useState([]);
    const [loading, setLoading] = useState(false);
    useEffect(() => {
        loadGalleryItems()
        }, []);   
    const loadGalleryItems = async () => {
        setLoading(true);
        let photos = await api.getAlbum();
        setLoading(false);
        setItems(photos)
    };
    return( 
        <>
            {loading && <div><h1>Carregando...</h1></div>}
            <div>    
                {!loading && items.length>0 &&
                <div>
                    <h1>Albums: {items.length}</h1>
                    <ul>
                        {
                            items.map((items, itemsKey) => (
                                <li className="p-3" key={itemsKey} style={{border: '1px solid black', margin: '10px'}}><h2>{items}</h2></li>
                            ))
                        }
                    </ul>
                </div>
                }
            </div>
        </>
    );
}
