import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { api } from "../../api";

export const GalleryItems = () => {
    const params = useParams();
    const [items, setItems] = useState([]);
    const [galleryInfo, setGalleryInfo] = useState({id:0, title:'', userId: 0});
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        if(params.id){
            loadGalleryItems(params.id);
            loadGalleryInfo(params.id);
        }   
        });   
    const loadGalleryItems = async (id) => {
        setLoading(true);
        let items = await api.getAllPhotosAlbum(id);
        setLoading(false);
        setItems(items)
    };
    const loadGalleryInfo = async (id) => {
        setLoading(true);
        let galleryInfo = await api.getAlbum(id);
        setGalleryInfo(galleryInfo)
    };
    return( 
        <>
            {loading && <div><h1>Carregando...</h1></div>}
            <div>    
                {!loading && items.length>0 &&
                <div>
                    <h1>Album: {galleryInfo}</h1>
                    <ul>
                        {
                            items.map((items, itemsKey) => (
                                <li className="p-3" key={itemsKey} style={{border: '1px solid black', margin: '10px'}}><h2>{items}</h2></li>
                            ))
                        }
                    </ul>
                </div>
                }
            </div>
        </>
    );
}
