import React, {useState} from "react";

const StyledCondition = () => {
    const [color, setColor] = useState('#FFF')
    const clickMeEvent = () => {
        setColor('blue')
    }
    return(
        <div>
            <button style={{backgroundColor: color}} onClick={clickMeEvent}>
                Click me
            </button>
        </div>
    );
}
export default StyledCondition
