import { useState, useEffect } from "react";

const EffectUsed = () => {
    const [name, setName] = useState ('Juan');

    useEffect(() =>{
        alert('Executou')
    }, [handleClick]);

    const handleClick = () => {
        setName('Pala')
    }

    return (
        <div>
            nome: { name }
            <button onClick={handleClick}>Clique aqui</button>
        </div>
    );
}
export default EffectUsed;
